    create: {//新建
       url: "...",
       contentType: "application/json",//定义数据类型
       type: "POST"//定义请求方式
    },
    update: {//修改
       url: "...",
       contentType: "application/json",
       type: "POST"
    },
    destroy: {//删除
       url: "...",
       contentType: "application/json",
       type: "POST"
    },
  },
  pageSize: 10 //每一页10条数据
});
$("#grid").kendoGrid({
  dataSource: dataSource,
  pageable: true //是否分页
});


